    <div>
<h1>{props.heading}</h1>
<div className="mb-3">
     <label for="myBox" class="form-label">MandipisLive want to text Here</label>
     <textarea className="form-control" id="myBox" rows="6"></textarea>
     <button className="btn btn primary">Convert to upper case</button>
</div>

    </div>
  )
}
