export default function TextForm(props) {
  const handleUpClick = () => {
  console.log("Uppercase was clicked");
  }

  const handleOnChange = () =>{
    console.log("On Change");
}

  const [text, setText] = useState('Hey FameTechz');
  return (
    <div>
<h1>{props.heading} - {text}</h1>
<div className="mb-3">
     <label for="myBox" className="form-label">MandipisLive want to text Here</label>
     <textarea className="form-control" value={text} onChannge={handleOnChange} id="myBox" rows="6"></textarea>
     <button className="btn btn-primary" onClick={handleUpClick}>Convert to upper case</button>
</div>

    </div>
  )
}
