import React, {useState} from 'react'

export default function TextForm(props) {
  const handleUpClick = () => {
  console.log("Uppercase was clicked" + text);
  let newText = text.toUpperCase();
  setText(newText);
  }

  const handleOnChange = (event) =>{
    console.log("On Change"); // ye console ko ham remove v kar skte hai
    setText(event.target.value);
}

  const [text, setText] = useState('Hey FameTechz');
  return (
    <>
<div className="container">
     <h1>{props.heading}</h1>
     <div className="mb-3">
     <label for="myBox" className="form-label">MandipisLive want to text Here</label>
     <textarea className="form-control" value={text} onChange={handleOnChange} id="myBox" rows="6"></textarea>
     </div>
     <button className="btn btn-primary my-2" onClick={handleUpClick}>Convert to upper case</button>
     </div>
     <div className="container my-3">
     <h1>Your Text Summary</h1>
     <p> 4232 words and {text.lenght} characters</p>

     </div>
    </>
  )
}
