import logo from './logo.svg';
import './App.css';
import Navbar from './components/Navbar';
import TextForm from './components/TextForm';
import About from './components/About';
import React, {useState} from 'react';
import Alert from './components/Alert';

let name = "MandipisLive FT"

function App() {
  const [mode, setMode] = useState('light'); //whether dark mode is enable is not
  const toggleMode = ()=>{
    if(mode === 'light'){
      setMode('dark');
      document.body.style.backgroundColor = 'grey';
    }
    else {
    setMode('light');
    document.body.style.backgroundColor = 'white';
    }

  }
  return (
  <> 
  <Navbar title="MandipisLive" aboutText="About" mode={mode} toggleMode={toggleMode} />
  <Alert/>
  <div className="container my-3">
  <TextForm heading="Enter Text to Change Format" mode={mode}/>
  <About/>
  </div>
  {/* <Navbar/> */}
  </>
  );
}

export default App;
